<script>
document.addEventListener('DOMContentLoaded', function() {
    // --- Phone Dropdown ---
    const trigger = document.getElementById('phones-trigger');
    const dropdown = document.getElementById('phones-dropdown');
    
    if (trigger && dropdown) {
        let isOpen = false;
        function toggleDropdown(show) {
            isOpen = show;
            if (show) {
                dropdown.classList.remove('invisible', 'opacity-0', 'scale-95');
                dropdown.classList.add('visible', 'opacity-100', 'scale-100');
            } else {
                dropdown.classList.add('invisible', 'opacity-0', 'scale-95');
                dropdown.classList.remove('visible', 'opacity-100', 'scale-100');
            }
        }
        trigger.addEventListener('click', function(e) { e.stopPropagation(); toggleDropdown(!isOpen); });
        document.addEventListener('click', function(e) { 
            if (!dropdown.contains(e.target) && e.target !== trigger) toggleDropdown(false); 
        });
        document.addEventListener('keydown', function(e) { if (e.key === 'Escape') toggleDropdown(false); });
    }

    // --- Mobile Menu ---
    const mobileBtn = document.getElementById('mobile-menu-toggle');
    const mobileMenu = document.getElementById('mobile-menu-overlay');
    
    if (mobileBtn && mobileMenu) {
        const bars = mobileBtn.querySelectorAll('span');
        
        mobileBtn.addEventListener('click', () => {
            const isClosed = mobileMenu.classList.contains('invisible');
            
            if (isClosed) {
                // Open
                mobileMenu.classList.remove('invisible', 'opacity-0');
                document.body.classList.add('overflow-hidden');
                
                // Burger -> Cross
                bars[0].classList.add('rotate-45', 'translate-y-[9px]');
                bars[1].classList.add('opacity-0');
                bars[2].classList.add('-rotate-45', '-translate-y-[9px]');
            } else {
                // Close
                mobileMenu.classList.add('invisible', 'opacity-0');
                document.body.classList.remove('overflow-hidden');
                
                // Cross -> Burger
                bars[0].classList.remove('rotate-45', 'translate-y-[9px]');
                bars[1].classList.remove('opacity-0');
                bars[2].classList.remove('-rotate-45', '-translate-y-[9px]');
            }
        });
    }
});
</script>